package welcomeToJava;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num;
//		number from one to five
		for(num = 1; num<=5; num=num+1)
		{
			
			System.out.println(num);
		}
			
//		number from 500 to one
		for(num = 500; num>=1; num = num-1)
		{
			 System.out.println(num);
		}

	}

}
